import './App.css';
import Post from './Post.js';
import React, { useEffect, useState } from "react";
import { auth, db } from './firebase'
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import { Button, Input } from '@material-ui/core';
import ImageUpload from './ImageUpload'

function getModalStyle() {
  const top = 50;
  const left = 50;

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}

const useStyles = makeStyles((theme) => ({
  paper: {
    position: 'absolute',
    width: 400,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

function App() {
  const [posts, setPosts] = useState([])
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [modalStyle] = useState(getModalStyle);
  const [username, setUsername] = useState('');
  const [openSignIn, setOpenSignIn] = useState(false)
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState(null)

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((authUser) => {
      if (authUser) {
        console.log(authUser)
        setUser(authUser)
      }
      else {
        setUser(null)
      }
    })
    return (() => {
      unsubscribe()
    })
  }, [user, username])


  useEffect(() => {
    db.collection('posts').orderBy('timestamp','desc').onSnapshot(snapshot => {
      setPosts(snapshot.docs.map(doc => ({
        post: doc.data(),
        id: doc.id
      })))
    })
  }, []);

  const signUp = (event) => {
    event.preventDefault();
    auth.createUserWithEmailAndPassword(email, password)
      .then((authUser) => {
        return authUser.user.updateProfile({
          displayName: username
        })
      })
      .catch((error) => alert(error.message));
    setOpen(false)

  }

  const signIn = (event) => {
    event.preventDefault();
    auth.signInWithEmailAndPassword(email, password)
      .catch((error) => alert(error.message))
    setOpenSignIn(false)
  }


  return (
    <div className="App">
      {user?.displayName ? (
        <ImageUpload username={user.displayName} />
      ):
      (
        <h3>Sorry you need to login to upload</h3>
      )}


      <Modal
        open={open}
        onClose={() => setOpen(false)}
      >
        <div style={modalStyle} className={classes.paper}>
          <form className="app__signup">
            <center>
              <img className="app__headerImage" src="https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"></img>
            </center>
            <Input type="text" placeholder="username" value={username} onChange={(e) => setUsername(e.target.value)}></Input>
            <Input type="email" placeholder="email" value={email} onChange={(e) => setEmail(e.target.value)}></Input>
            <Input type="password" placeholder="password" value={password} onChange={(e) => setPassword(e.target.value)}></Input>
            <Button onClick={signUp}>Sign Up</Button>

          </form>
        </div>
      </Modal>
      <Modal
        open={openSignIn}
        onClose={() => setOpenSignIn(false)}
      >
        <div style={modalStyle} className={classes.paper}>
          <form className="app__signup">
            <center>
              <img className="app__headerImage" src="https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"></img>
            </center>
            <Input type="email" placeholder="email" value={email} onChange={(e) => setEmail(e.target.value)}></Input>
            <Input type="password" placeholder="password" value={password} onChange={(e) => setPassword(e.target.value)}></Input>
            <Button onClick={signIn}>Sign In</Button>

          </form>
        </div>
      </Modal>
      <div className="app__header">
        <img className="app__headerImage" src="https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"></img>
      </div>
      {
        user ? (<Button onClick={() => auth.signOut()}>Log Out</Button>)
          :
          (<div className="app__loginContainer">
            <Button onClick={() => setOpenSignIn(true)}>Sign In</Button>
            <Button onClick={() => setOpen(true)}>Sign Up</Button>
          </div>
          )
      }

      {
        posts.map(({ post, id }) => {
          return <Post key={id} username={post.username} caption={post.caption} imageUrl={post.imageUrl} />
        })
      }
    </div>
  );
}

export default App;
